var Q=require("q"),path=require("path"),shell=require("shelljs"),spawn=require("./spawn"),projectPath=path.join(__dirname,"..","..");module.exports.run=function(){var e=shell.ls(projectPath).filter(function(e){return".xcodeproj"===path.extname(e)})[0];return e?spawn("xcodebuild",["-project",e,"-configuration","Debug","-alltargets","clean"],projectPath).then(function(){return spawn("xcodebuild",["-project",e,"-configuration","Release","-alltargets","clean"],projectPath)}).then(function(){return shell.rm("-rf",path.join(projectPath,"build"))}):Q.reject("No Xcode project found in "+projectPath)};